import React from "react";
import Calendar from "react-calendar";
import moment from "moment";

class CalenderComponentClass extends React.Component {
  constructor(props) {
    super(props);
    const myEvents = localStorage.getItem("myEvents");
    this.state = {
      calenderDate: new Date(),
      myEvents,
      eventValue: "",
      addEvent: false,
    };
  }

  handleChangeDate = (value, event) => {
    console.log(value);
    console.log(event);
    this.setState({ calenderDate: new Date(), addEvent: true });
  };

  handleChange = (name) => (event) => {
    console.log(name);
    console.log(event.target.value);
    this.setState({
      eventValue: event.target.value,
    });
  };

  saveData = () => {
    var momentDate = moment(this.state.calenderDate).format("l");
    console.log(momentDate);
    var data = {
      date: momentDate,
      value: this.state.eventValue,
    };
    console.log(data);
    let myEventData = [];
    myEventData.push(data);
    console.log(myEventData);
    localStorage.setItem("myEvents", JSON.stringify(data));
  };
  render() {
    return (
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column" }}>
        <div>
          <Calendar
            activeStartDate={new Date()}
            style={{ height: "40vh", width: "40vh" }}
            allowPartialRange={true}
            onChange={(value, event) => this.handleChangeDate(value, event)}
            activeStartDate={new Date(2021, 1, 1)}
            defaultActiveStartDate={new Date(2021, 1, 1)}
            defaultValue={new Date()}
            value={this.state.calenderDate}
          />
        </div>
        {this.state.addEvent ? (
          <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
            <input
              style={{ marginTop: "10%", width: "45vh", height: "30px" }}
              type="text"
              id="eventValue"
              name="eventValue"
              placeholder="Add Your Event"
              value={this.state.eventValue}
              onChange={this.handleChange("eventValue")}
            />
            <input
              style={{ height: "5vh", width: "20vh", marginTop: "6%" }}
              type="submit"
              value="Submit"
              onClick={() => this.saveData()}
            />
          </div>
        ) : (
          ""
        )}
      </div>
    );
  }
}

export default CalenderComponentClass;
