import React from "react";
import Calendar from "react-calendar";

class CalenderComponentClass extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calenderDate: new Date(),
    };
  }

  handleChangeDate = () => {
    this.setState({ calenderDate: new Date() });
  };
  render() {
    return (
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          style={{ height: "40vh", width: "40vh" }}
          onChange={this.handleChangeDate}
          activeStartDate={new Date(2020, 0, 12)}
          defaultActiveStartDate={new Date()}
          value={this.state.calenderDate}
        />
      </div>
    );
  }
}

export default CalenderComponentClass;
