import React from "react";
import Calendar from "react-calendar";
import moment from "moment";

class CalenderComponentClass extends React.Component {
  constructor(props) {
    super(props);
    const myEvents = localStorage.getItem("myEvents");
    this.state = {
      calenderDate: new Date(),
      myEvents,
      eventValue: "",
      addEvent: false,
    };
  }

  handleChangeDate = (value, event) => {
    var currentDate = moment(new Date()).format("l");
    var eventDate = moment(value).format("l");
    console.log(currentDate);
    console.log(eventDate);
    console.log((currentDate = new Date(eventDate)));
    // var firebaseSessionTime = singleSessionData.dateTime.seconds * 1000;
    // var same = d1.getTime() === d2.getTime();
    // console.log(same);
    console.log(value);
    console.log(this.state.myEvents);
    this.setState({ calenderDate: new Date(), addEvent: true });
  };

  handleChange = (name) => (event) => {
    this.setState({
      eventValue: event.target.value,
    });
  };

  saveData = () => {
    var momentDate = moment(this.state.calenderDate).format("l");
    var data = {
      [momentDate]: [this.state.eventValue],
    };
    console.log(data);
    let myEventData = [];
    myEventData.push(data);
    console.log(myEventData);
    localStorage.setItem("myEvents", JSON.stringify(data));
  };

  render() {
    return (
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column" }}>
        <div>
          <Calendar
            // minDate={new Date()}
            showNavigation={false}
            showNeighboringMonth={false}
            activeStartDate={new Date()}
            style={{ height: "40vh", width: "40vh" }}
            allowPartialRange={true}
            onChange={(value, event) => this.handleChangeDate(value, event)}
            // activeStartDate={new Date(2021, 1, 1)}
            // defaultActiveStartDate={new Date(2021, 1, 1)}
            defaultValue={new Date()}
            value={this.state.calenderDate}
          />
        </div>
        {this.state.addEvent ? (
          <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
            <div style={{ display: "flex", alignItems: "flex-end" }}>
              <input
                style={{ marginTop: "10%", width: "45vh", height: "30px", marginRight: "4%" }}
                type="text"
                id="eventValue"
                name="eventValue"
                placeholder="Add Your Event"
                value={this.state.eventValue}
                onChange={this.handleChange("eventValue")}
              />
              <div
                style={{
                  borderRadius: "50%",
                  border: "1px solid blue",
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  width: "30px",
                  height: "30px",
                  color: "blue",
                }}
                onClick={() => this.saveData()}
              >
                +
              </div>
            </div>
            {/* <input
              style={{ height: "5vh", width: "20vh", marginTop: "6%" }}
              type="submit"
              value="Save"
              onClick={() => this.saveData()}
            /> */}
          </div>
        ) : (
          ""
        )}
      </div>
    );
  }
}

export default CalenderComponentClass;
